import React, { Component, useRef } from "react";
import { Link } from "react-router-dom";
import { Nav, Tab, Modal, Button } from "react-bootstrap";
import Header from "../layout/header";
import Footer from "../layout/footer";
import Brand1 from "../element/brand1";
import axios from "axios";

const aboutbg = require("./../../assets/images/background/image-11.jpg");
const touchbg = require("./../../assets/images/background/image-8.jpg");

class CreatePost extends Component {
  state = {
    // Initially, no file is selected
    selectedFile: null,
  };

  // On file select (from the pop up)
  onFileChange = (event) => {
    // Update the state
    this.setState({ selectedFile: event.target.files[0] });
  };

  // On file upload (click the upload button)
  onFileUpload = () => {
    // Create an object of formData
    const formData = new FormData();

    // Update the formData object
    formData.append("file", this.state.selectedFile);

    // Details of the uploaded file
    console.log(this.state.selectedFile);

    // Request made to the backend api
    // Send formData object
    axios.post("data/upload", formData);
  };

  render() {
    return (
      <>
        <Header />
        {/* <!-- Page Banner Section --> */}
        <section class="page-banner">
          <div
            className="page-banner-bg"
            style={{ backgroundImage: "url(" + aboutbg + ")" }}
          ></div>
          <div class="bottom-rotten-curve alternate"></div>

          <div class="auto-container">
            <h1>Posts</h1>
            <ul class="bread-crumb clearfix">
              <li>
                <Link to={"/#"}>Home</Link>
              </li>
              <li class="active">Posts</li>
            </ul>
          </div>
        </section>
        <div>
          <div className="col-sm-auto">
            <div className="btn-group">
              <div onClick={this.handleUpload}>
                <div style={{ cursor: "pointer" }}>
                  <i
                    className="uil uil-plus-circle"
                    style={{ fontSize: "50px" }}
                  ></i>
                </div>
              </div>
              <input
                accept="image/*"
                id="upload"
                ref={this.hiddenFileInput}
                type="file"
                className="bi bi-paperclip fs-18"
                name="myFile"
                style={{ display: "none" }}
                onChange={this.handleFileChange}
              />
            </div>
          </div>
        </div>
      </>
    );
  }
}
export default CreatePost;
