import React, { Component, useRef } from "react";
import { Link } from "react-router-dom";
import { Row, Col } from "react-bootstrap";
import Header from "../layout/header";
import Footer from "../layout/footer";
import Brand1 from "../element/brand1";
import axios from "axios";
import VideoInput from "../layout/VideoInput";

const aboutbg = require("./../../assets/images/background/image-11.jpg");
const touchbg = require("./../../assets/images/background/image-8.jpg");

class UploadEpisode extends Component {
  state = {
    // Initially, no file is selected
    selectedFile: null,
    fileURL: "",
    title: "",
    post: "",
  };
  inputRef = React.useRef();

  render() {
    return (
      <>
        <Header />
        {/* <!-- Page Banner Section --> */}
        <section class="page-banner">
          <div
            className="page-banner-bg"
            style={{ backgroundImage: "url(" + aboutbg + ")" }}
          ></div>
          <div class="bottom-rotten-curve alternate"></div>

          <div class="auto-container">
            <h1>Episodes</h1>
            <ul class="bread-crumb clearfix">
              <li>
                <Link to={"/#"}>Home</Link>
              </li>
              <li class="active">Upload Videos</li>
            </ul>
          </div>
        </section>
        <Row>
          <div className="VideoInput">
            <input
              ref={inputRef}
              className="VideoInput_input"
              type="file"
              onChange={handleFileChange}
              accept=".mov,.mp4"
            />
            {!source && (
              <i
                onClick={handleChoose}
                style={{ fontSize: "50px", padding: "5%", color: "grey" }}
                class="fa fa-plus"
              ></i>
            )}
            {source && (
              <video
                className="VideoInput_video"
                width="100%"
                height={height}
                controls
                src={source}
              />
            )}
            <div className="VideoInput_footer">
              {source || "Nothing selectd"}
            </div>
          </div>
          <div class="contact-form" style={{ width: "80%", margin: "auto" }}>
            <div class="row clearfix">
              <div class="col-md-6 form-group">
                <label for="name">Enter your name</label>
                <input
                  type="text"
                  name="username"
                  id="name"
                  placeholder="Enter name here......"
                  required=""
                  onChange={(e) => {
                    this.setState({ name_1: e.target.value });
                  }}
                />
                <i class="fas fa-user"></i>
              </div>
              <div class="col-md-6 form-group">
                <label for="name">Enter your name</label>
                <input
                  type="text"
                  name="username"
                  id="name"
                  placeholder="Enter name here......"
                  required=""
                  onChange={(e) => {
                    this.setState({ name_1: e.target.value });
                  }}
                />
                <i class="fas fa-user"></i>
              </div>
            </div>
          </div>
        </Row>
        <Footer />
      </>
    );
  }
}
export default UploadEpisode;
